export const BASE_URL = "https://api.mesto.kuchevskaya.nomoredomains.xyz";

const checkResponse = (res) => {
  return res.ok ? res.json() : Promise.reject(`Ошибка: ${res.status}`);
};

export const register = (email, password, token) => {
  return fetch(`${BASE_URL}/signup`, {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    },
    credentials: 'include', // т.к. разрешила в браузере запросы c Allow-Credentials
    body: JSON.stringify({
      email,
      password,
    }),
  }).then(checkResponse);
};

export const authorize = (email, password, token) => {
  return fetch(`${BASE_URL}/signin`, {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    },
    credentials: 'include', // т.к. разрешила в браузере запросы c Allow-Credentials
    body: JSON.stringify({
      email,
      password,
    }),
  }).then(checkResponse);
};

export const getContent = (token) => {
  return fetch(`${BASE_URL}/users/me`, {
    method: "GET",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    },
    credentials: 'include', // т.к. разрешила в браузере запросы c Allow-Credentials
  }).then(checkResponse);
};
